import React, { Component } from "react";
import data from './data/medicines.json';
import 'bootstrap/dist/css/bootstrap.min.css';
//import './medicine.css';
export default class Medicine extends Component {
    render() {
        const match = this.props.match;
        let id = match.params.id;
        const newdata = data.filter(item => item.id === id);
        let medObj;
        if (newdata && newdata.length === 1) {
            medObj = newdata[0];
        }
        return (<div className="container"><div className="row"><div className="col-lg-2"><img className="medicine_image" src={medObj.image} alt={medObj.name} /></div>
            <div className="col-lg-10"><p className="medicine_name">{medObj.name}</p>
                <p className="medicine_description">{medObj.description}&nbsp;</p></div>
        </div></div >)
    }
}
