import React, { Component } from "react";
import Medicine from './components/medicine';
import { Route, Link, BrowserRouter as Router } from 'react-router-dom';
export default class MedicineList extends Component {
    render() {
        let data = this.props.data;
        console.log(data)
        const newdata = data.map(data => {
            return (
                <li key={data.id} className="medicine"><div className="row">
                    <div className="col-lg-2"><img className="medicine_image" src={data.image} alt={data.name} /></div>
                    <div className="col-lg-10"><p className="medicine_name">{data.name}</p>
                        <p className="medicine_description">{data.description}&nbsp;test<Link to={"/medicines/" + data.id}>See more &raquo;</Link></p></div>
                </div></li>
            );
        });
        return (<div className="container"><h2 className="medicines_heading">Medicines</h2><ul className="medicines_list">{newdata}</ul>
            <Route path="/medicines/:id" component={Medicine} /></div>);
    }
}
