import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import data from './data/medicines.json';
//import MedicineCarousel from './components/medicine_carousel'
function App() {
  const newdata = data.map(item => {
    return (
      <li key={item.id} className="medicine"><div className="row">
        <div className="col-lg-3"><img className="medicine_image" src={item.image} alt={item.name} /></div>
        <div className="col-lg-9"><p className="medicine_name">{item.name}</p>
          <p className="medicine_description">{item.description}</p></div>
      </div></li>
    );
  });
  return (<div className="container"><b>Medicines</b><ul className="medicines_list">{newdata}</ul></div>);
  //return (<MedicineCarousel/>)
}

export default App;
